=arrayformula(iferror(IF(isblank(iferror(DATE(mid(VLOOKUP(A1:A40, roster_seis!$A$1:$Z, 11, 0),7,4), mid(VLOOKUP(A1:A40, roster_seis!$A$1:$Z, 11, 0),1,2),mid(VLOOKUP(A1:A40, roster_seis!$A$1:$Z, 11, 0),4,2)))), if(and(not(isblank(H1:H40)), not(isblank(A1:A40))),H1:H40 + 60, IF(isblank(A1:A40), "", "NA")), DATE(mid(VLOOKUP(A1:A40, roster_seis!$A$1:$Z, 11, 0),7,4), mid(VLOOKUP(A1:A40, roster_seis!$A$1:$Z, 11, 0),1,2),mid(VLOOKUP(A1:A40, roster_seis!$A$1:$Z, 11, 0),4,2))),))